pragma solidity ^0.4.23;

import "./BaseAdmin.sol";
import "./SafeMath.sol";

interface IPoolReader {
    function getDevCacheBalance() external view returns(uint256);
    function getPoolBLinkBalance() external view returns(uint256);
}

interface IBLinkToken {
    function balanceOf(address _from) external view returns(uint256);
    function transfer(address _to, uint256 _value) external returns(bool);
    function transferFrom(address _from, address _to, uint256 _value) external returns(bool);
    function burn(uint256 _value) external returns (bool);
}
contract BLinkDev is BaseAdmin {
    using SafeMath for uint256;

    struct GamePromotion {
        uint64 gameId;          // 游戏Id
        uint64 rate;            // 挖矿倍率(万分比) every 10000
        uint64 start;           // 开始时间
        uint64 end;             // 截止时间
    }

    IBLinkToken public blinkToken;
    IPoolReader public blinkPool;
    address public addrOperator;       // the operator
    address public addrTokenDev;       // 开发者主账号
    address public addrDevChild;       // 开发者次账户

    uint256 public devMinePercent = 49;       // user: 100 - 49 = 51, dev: 49
    uint256 public globalPromotion = 0;       // 活动倍率(万分比) // promotion add
    uint64 public globalPromotionStart = 0;
    uint64 public globalPromotionEnd = 0;

    mapping (uint256 => GamePromotion) gamePromotionMap;// 指定游戏挖矿比例

    constructor() public {
        addrAdmin = msg.sender;
        addrOperator = msg.sender;
    }

    modifier onlyOperator() {
        require(msg.sender == addrAdmin || msg.sender == addrOperator);
        _;
    }

    function setOperator(address _newOperator) external onlyAdmin {
        require(_newOperator != address(0));
        addrOperator = _newOperator;
    }

    function getAddrParam() external view returns(address diceAddr, address poolAddr) {
        diceAddr = address(blinkToken);
        poolAddr = address(blinkPool);
    }

    function setAddrParam(address _addrBLink, address _addrPool) external onlyAdmin {
        require(_addrBLink != address(0) && _addrPool != address(0));
        blinkToken = IBLinkToken(_addrBLink);
        blinkPool = IPoolReader(_addrPool);
    }

    function setTokenDevAddr(address _addr) external onlyAdmin {
        require(_addr != address(0));
        addrTokenDev = _addr;
    }

    function setDevChildAddr(address _addr) external onlyAdmin {
        require(_addr != address(0));
        addrDevChild = _addr;
    }

    function setDevMinePercent(uint256 _per) external onlyAdmin {
        require(_per > 0 && _per <= 60);
        devMinePercent = _per;
    }

    function setGlobalPromotion(uint64 _start, uint64 _end, uint64 _rate) external onlyOperator {
        require(_end > _start);
        require(_rate >= 5000 && _rate <= 50000);
        globalPromotion = _rate;
        globalPromotionStart = _start;
        globalPromotionEnd = _end;
    }

    function setGamePromotion(uint256 _gameId, uint64 _start, uint64 _end, uint64 _rate) external onlyOperator {
        require(_gameId > 0 && _end > _start);
        require(_rate == 0 || (_rate >= 5000 && _rate <= 50000));
        if (_rate != 0) {
            GamePromotion memory setting = GamePromotion(uint64(_gameId), _rate, _start, _end);
            gamePromotionMap[_gameId] = setting;
        } else {
            delete gamePromotionMap[_gameId];
        }
    }

    function getGlobalPromotion() external view returns(uint64 start, uint64 end, uint64 rate) {
        start = globalPromotionStart;
        end = globalPromotionEnd;
        rate = uint64(rate);
    }

    function getGamePromotion(uint256 _gameId) external view returns(uint256 gameId, uint64 start, uint64 end, uint64 rate) {
        if (_gameId > 0) {
            GamePromotion memory setting = gamePromotionMap[_gameId];
            gameId = _gameId;
            start = setting.start;
            end = setting.end;
            rate = setting.rate;
        }
    }

    function getDevBLinkAmount() external view returns(uint256) {
        uint256 devBLink = blinkPool.getDevCacheBalance();
        uint256 walletDev = blinkToken.balanceOf(addrTokenDev);//如果钱提走了，需要设置为财务的账户

        devBLink = devBLink.add(walletDev);
        if (addrDevChild != address(0)) {
            uint256 walletChild = blinkToken.balanceOf(addrDevChild);
            if (walletChild > 0) {
                devBLink = devBLink.add(walletChild);
            } 
        }
        return devBLink;
    }

    function getMiningBLink(uint256 _gameId, address _miner, uint256 _amount)
        external 
        view 
        returns(
            uint256 _minerCnt,
            uint256 _devCnt
        )
    {
        uint256 blinkBalance = blinkPool.getPoolBLinkBalance();
        if (blinkBalance == 0) {
            _minerCnt = 0;
            _devCnt = 0;
            return;
        }
        uint64 tmNow = uint64(block.timestamp);
        uint256 mineCount = _amount / (996 - ((blinkBalance - 1) / 5000000000000).mul(4));//核心挖矿算法
        if (globalPromotion > 0 && tmNow >= globalPromotionStart && tmNow <= globalPromotionEnd) {
            mineCount = mineCount.mul(globalPromotion) / 10000;
        } else {
            GamePromotion memory setting = gamePromotionMap[_gameId];
            if (setting.rate > 0 && tmNow >= setting.start && tmNow <= setting.end) {
                mineCount = mineCount.mul(uint256(setting.rate)) / 10000;
            }
        }
        uint256 devCount = mineCount.mul(devMinePercent) / (100 - devMinePercent);
        if ((mineCount.add(devCount)) > blinkBalance) {
            _minerCnt = blinkBalance.mul(100 - devMinePercent).div(100);
            _devCnt = blinkBalance.sub(_minerCnt);
        } else {
            _minerCnt = mineCount;
            _devCnt = devCount;
        }
    }
}
