pragma solidity ^0.4.23;

import "./BaseAdmin.sol";

interface IBLinkDice {
    function __callback(address _bettor, uint256 _seed) external;
}

contract BLinkDiceOraclize is BaseAdmin {
    IBLinkDice blinkDice;

    mapping (address => bool) public safeBetContracts;

    constructor() public {
        addrAdmin = msg.sender;
    }

    function setSafeContract(address _addr, bool _useful) external onlyAdmin {
        require(_addr != address(0));
        safeBetContracts[_addr] = _useful;
    }

    function setBLinkDice(address _addr) external onlyAdmin {
        require(_addr != address(0));
        blinkDice = IBLinkDice(_addr);
    }

    function endRollDice(address _bettor, uint256 _seed) external {
        require(safeBetContracts[msg.sender] || msg.sender == addrAdmin);
        blinkDice.__callback(_bettor, _seed);
    }

}
