pragma solidity ^0.4.23;

import "./BaseAdmin.sol";
import "./SafeMath.sol";

interface IBLinkToken {
    function balanceOf(address _from) external view returns(uint256);
    function transfer(address _to, uint256 _value) external returns(bool);
    function transferFrom(address _from, address _to, uint256 _value) external returns(bool);
    function burn(uint256 _value) external returns (bool);
}

/*
    BLink矿池
*/
contract BLinkMinePool is FinanceAdmin {
    using SafeMath for uint256;
    IBLinkToken blinkToken;
    mapping (address => bool) public safeBetContracts;

    constructor() public {
        addrAdmin = msg.sender;
    }

    function() external payable {

    }

    modifier whenNotPaused() {
        require(!isPaused);
        _;
    }

    function transferBLink(address _to, uint256 _amount)
    external
    whenNotPaused
    {
        require(safeBetContracts[msg.sender]);
        if (_amount == 0) {
            return;
        }
        uint256 ownBalance = blinkToken.balanceOf(address(this));
        require(ownBalance >= _amount);
        blinkToken.transfer(_to, _amount);
    }

    function setSafeContract(address _actionAddr, bool _useful) external onlyAdmin {
        safeBetContracts[_actionAddr] = _useful;
    }

    function withdrawProfit(uint256 _amount)
    external
    {
        require(safeBetContracts[msg.sender]);
        require(_amount > 0 && _amount <= address(this).balance);
        addrFinance.transfer(_amount);
    }

    function transferToken(address _tokenAddr, address _to, uint256 _amount )
    external
    whenNotPaused
    {
        require(safeBetContracts[msg.sender]);
        IBEP20 token = IBEP20(_tokenAddr);
        uint256 tokenBalance = token.balanceOf(address(this));
        token.transfer(_to, _amount);
    }

}
