pragma solidity ^0.4.23;

import "./BaseAdmin.sol";
import "./SafeMath.sol";

/*
    从TRON链上读取用户质押的win，dice，live  token然后 根据BSC链的盈利，将钱打给用户
*/
contract BLinkWinkDividend is FinanceAdmin {
    using SafeMath for uint256;

    mapping (address => bool) public safeBetContracts;
    mapping (uint256 => bool) public confirmedTxMap;

    constructor() public {
        addrAdmin = msg.sender;
    }

    function() external payable {

    }

    modifier whenNotPaused() {
        require(!isPaused);
        _;
    }

    function setSafeContract(address _actionAddr, bool _useful) external onlyAdmin {
        safeBetContracts[_actionAddr] = _useful;
    }

    /*
        后台获取本合约的余额，然后根据用户抵押的数量来进行计算给用户分红的数量,调用合约完成分红
    */
    function dividendToUser(address to,address[] memory tokenAddrs, uint256[] memory _tokenValues,uint256 _sysTxId){
        require(safeBetContracts[msg.sender]);
        require(!confirmedTxMap[_sysTxId]);
        confirmedTxMap[_sysTxId] = true;
        to.transferAll(tokenAddrs, _tokenValues);
    }

}
