pragma solidity ^0.4.23;

import "./BaseAdmin.sol";
import "./SafeMath.sol";

interface IBLinkToken {
    function balanceOf(address _from) external view returns(uint256);
    function transfer(address _to, uint256 _value) external returns(bool);
    function transferFrom(address _from, address _to, uint256 _value) external returns(bool);
    function burn(uint256 _value) external returns (bool);
}

interface IBLinkMinePool{
    function transferBLink(address _to, uint256 _amount) external;
}

/*
    BLink 庄家底池
*/
contract BLinkPool is FinanceAdmin {
    using SafeMath for uint256;

    address public addrTokenDev;
    address public addrBurner;
    address public addrDivider;//BLink抵押分红合约
    address public addrWinkDivider;//Wink抵押分红合约

    IBLinkToken blinkToken;

    /*
        矿池
    */
    IBLinkMinePool blinkMinePool;

    uint256 public safeWaterLevel = 1000;
    uint256 public blinkTotalCache;
    uint256 public blinkDevCache;

    /*
         bsc上盈利的token 将会有一半给win的抵押者持有
    */
    uint256 public winkRate=50;
    mapping (address => bool) public safeBetContracts;
    mapping (address => uint256) public playerBLinkCache;

    mapping (address => uint256) public tokenOriginalAmountMap;


    event TokenBurn(
        address indexed _from,
        address indexed _addrToken,
        uint256 _value
    );

    event TokenDivide(
        uint256 indexed _index,
        address addrToken,
        uint256 tokenValue
    );

    constructor() public {
        addrTokenDev = msg.sender;
        addrBurner = msg.sender;
        tokenOriginalAmountMap[address(0)]=10000*1e18;
    }

    function() external payable {

    }

    modifier whenNotPaused() {
        require(!isPaused);
        _;
    }

    function setBurner(address _newBurner) external onlyAdmin {
        require(_newBurner != address(0));
        addrBurner = _newBurner;
    }
    function setAddrDivider(address _addr) external onlyAdmin {
        require(_addr!= address(0));
        addrDivider= _addr;
    }

    function setWinkDivider(address _newWinkDivider) external {
        require(msg.sender == _newWinkDivider || msg.sender == addrAdmin);
        require(_newWinkDivider != address(0));
        addrWinkDivider = _newWinkDivider;
    }

    function setWinkRate(uint256 _newRate)external onlyAdmin {
        require(_newRate>0 && _newRate<100);
        winkRate=_newRate;
    }

    function setSafeContract(address _actionAddr, bool _useful) external onlyAdmin {
        safeBetContracts[_actionAddr] = _useful;
    }

    function getSafeContract(address _actionAddr) external view returns(bool) {
        return safeBetContracts[_actionAddr];
    } 

    function setTokenDevAddr(address _addr) external onlyAdmin {
        require(_addr != address(0));
        addrTokenDev = _addr;
    }

    function setBLinkTokenAddr(address _addr) external onlyAdmin {
        require(_addr != address(0));
        blinkToken = IBLinkToken(_addr);
    }

    function getBLinkTokenAddr() external view returns(address) {
        return address(blinkToken);
    }

    function setBLinkMinePool(address _addr) external onlyAdmin{
        require(_addr != address(0));
        blinkMinePool=IBLinkMinePool(_addr);
    }

    function getBLinkMinePool() external view returns(address) {
        return address(blinkMinePool);
    }

    function getTokenDevAddr() external view returns(address) {
        return addrTokenDev;
    }

    function setSafeWaterLevel(uint256 _newLevel) external{
        require(safeBetContracts[msg.sender]);
        require(_newLevel > 0 && _newLevel <= 5000);
        safeWaterLevel = _newLevel;
    }

    function setTokenOriginalAmount(address _tokenAddr, uint256 _amount){
        require(safeBetContracts[msg.sender]);
        tokenOriginalAmountMap[_tokenAddr] = _amount;
    }

    function transferBnb(address _to, uint256 _amount, uint256 _originalAmount) external whenNotPaused returns(bool) {
        require(safeBetContracts[msg.sender]);
        require((_amount - _originalAmount) <= (address(this).balance / 10000 * safeWaterLevel));
        _to.transfer(_amount);
        return true;
    }

    function transferToWiner(address _tokenAddr, address _to, uint256 _amount, uint256 _original) external whenNotPaused returns(bool) {
        require(safeBetContracts[msg.sender]);
        if(_tokenAddr==address(0)){
            require(_amount.sub(_original) <= (address(this).balance / 10000 * safeWaterLevel));
            _to.transfer(_amount);
        }else{
            IBEP20 token = IBEP20(_tokenAddr);
            uint256 tokenBalance = token.balanceOf(address(this));
            require(_amount.sub(_original) <= (tokenBalance / 10000).mul(safeWaterLevel));
            token.transfer(_to, _amount);
        }
        return true;
    }

    function getPoolBLinkBalance() external view returns(uint256) {
        uint256 ownBalance = blinkToken.balanceOf(address(blinkMinePool));
        if (ownBalance >= blinkTotalCache) {
            return ownBalance - blinkTotalCache;
        } else {
            return 0;
        }
    }

    function getDevCacheBalance() external view returns(uint256) {
        return blinkDevCache;
    }

    function getTotalCacheBalance() external view returns(uint256) {
        return blinkTotalCache;
    }

    function getPoolBnbCount() external view returns(uint256) {
        return address(this).balance/1e18;
    }

    function transferBLinkWithDevRelease(address _to1, uint256 _amount, uint256 _devReleaseAmount)
        external 
        whenNotPaused 
    {
        require(safeBetContracts[msg.sender] && _to1 != address(0));
        uint256 ownBalance = blinkToken.balanceOf(address(blinkMinePool));
        uint256 realBalance = ownBalance.sub(blinkTotalCache);
        uint256 totalAmount = _amount.add(_devReleaseAmount);
        require(realBalance >= totalAmount);
        if (_amount > 0) {
            uint256 oldToVal = playerBLinkCache[_to1];
            playerBLinkCache[_to1] = oldToVal.add(_amount);
        }
        blinkTotalCache=blinkTotalCache.add(totalAmount);
        blinkDevCache=blinkDevCache.add(_devReleaseAmount);
    }

    function releaseDevBLink() external {
        require(msg.sender == addrFinance || msg.sender == addrAdmin || msg.sender == addrTokenDev);   
        require(blinkDevCache> 0 && blinkTotalCache>= blinkDevCache);

        blinkDevCache=0;
        blinkMinePool.transferBLink(addrTokenDev, blinkDevCache);
    }

    function releaseBLink(address _owner) external {
        require(msg.sender == operator || msg.sender == addrAdmin || safeBetContracts[msg.sender]);
        require(_owner != address(0));
        uint256 cacheVal = playerBLinkCache[_owner];
        require(cacheVal > 1);
        require(cacheVal <= blinkTotalCache);
        blinkTotalCache=blinkTotalCache.sub(cacheVal - 1);
        playerBLinkCache[_owner] = 1;
        blinkMinePool.transferBLink(_owner, cacheVal - 1);
    }

    function releaseBLinkSelf() external {
        uint256 cacheVal = playerBLinkCache[msg.sender];
        require(cacheVal > 1);
        require(cacheVal <= blinkTotalCache);
        blinkTotalCache=blinkTotalCache.sub(cacheVal - 1);
        playerBLinkCache[msg.sender] = 1;
        blinkMinePool.transferBLink(msg.sender, cacheVal - 1);
    }

    function withdrawProfit(uint256 _amount)
        external 
    {
        require(msg.sender == addrFinance || msg.sender == addrAdmin);
        require(_amount > 0 && _amount <= address(this).balance);
        addrFinance.transfer(_amount);    
    }

    function transferToken(address _tokenAddr, address _to, uint256 _amount, uint256 _original)
    external
    whenNotPaused
    {
        require(safeBetContracts[msg.sender]);
        IBEP20 token = IBEP20(_tokenAddr);
        uint256 tokenBalance = token.balanceOf(address(this));
        require(_amount.sub(_original) <= (tokenBalance / 10000).mul(safeWaterLevel));
        token.transfer(_to, _amount);
    }

    function burnToken(address _addrToken) external returns(uint256) {
        require(msg.sender == addrBurner || msg.sender == addrAdmin);
        IBEP20 token = IBEP20(_addrToken);
        uint256 tokenBalance = token.balanceOf(address(this));
        uint256 tokenProfit = tokenBalance.sub(tokenOriginalAmountMap[_addrToken]);
        require(tokenBalance > tokenProfit);
        if (tokenProfit > 0) {
            token.burn(tokenProfit);
            emit TokenBurn(address(this), _addrToken, tokenProfit);
            return tokenProfit;
        } else {
            return 0;
        }
    }

    function transferToDivide(address[] _addrTokens) external {
        require(msg.sender == operator || safeBetContracts[msg.sender] || msg.sender == addrAdmin);
        uint256 len = _addrTokens.length;
        address tokenAddr;
        for(uint256 i = 0; i < len; i += 1){
            _transferTokenToDivide(_addrTokens[i],block.timestamp);
        }
    }

    function _transferTokenToDivide(address _addrToken,uint256 _index) internal{
        uint256 originalVal = tokenOriginalAmountMap[_addrToken];
        require(originalVal > 0);
        uint256 curBalance;
        IBEP20 token ;
        if(_addrToken==address(0)){
            curBalance=address(this).balance;
        }else{
            token= IBEP20(_addrToken);
            curBalance= token.balanceOf(address(this));
        }
        uint256 val =0;
        uint256 valWin =0;

        if(curBalance>originalVal){
            val = curBalance - originalVal;
            valWin=val.mul(winkRate).div(100);
            if(_addrToken==address(0)){
                addrWinkDivider.transfer(valWin);
                addrDivider.transfer(val-valWin);
            }else{
                token.transfer(addrWinkDivider, valWin);
                token.transfer(addrDivider, val-valWin);
            }
        }
        emit TokenDivide(_index, _addrToken, val-valWin);
    }

    function withdrawToken(address _addrToken, uint256 _amount) external onlyAdmin {
        IBEP20 token = IBEP20(_addrToken);
        uint256 tokenBalance = token.balanceOf(address(this));
        uint256 wAmount = _amount;
        if (_amount > tokenBalance) {
            wAmount = tokenBalance;
        }
        token.transfer(addrFinance, wAmount);
    }

}
