pragma solidity ^0.4.23;

import "./BaseAdmin.sol";
import "./SafeMath.sol";
import "./AddressUtil.sol";

interface IBLinkStakerContract {
    function setStakePause(bool _pause) external;
    function getTotalStakeBLink() external view returns(uint256 devAmount, uint256 totalAmount);
    function getMaxHolderIndex() external view returns(uint256);
    function getBLinkAmountByIndex(uint256 _index) external view returns(address owner, uint256 blinkAmount);
}

contract BLinkDividend is FinanceAdmin {
    using SafeMath for uint256;
    using SafeMath64 for uint64;
    using AddressUtil for address;

    event SnapshotStarted(uint32 snapshotVersion, uint256 totalStakingAmount, uint256 devStakingAmount, uint32 stakerCnt);
    event SnapshotCompleted(uint32 snapshotVersion, uint256 totalStakingAmount, uint256 devStakingAmount, uint32 stakingInfoLength);
    event BLinkStakingDividendCreate(uint256 indexed round,uint256 totalStakingAmount, address[] tokenAddrs, uint256[] tokenValues, uint32 stakingInfoLength, uint32 start);
    event BLinkStakingDividendStep(uint256 indexed round, uint256 indexed dividIndex, address indexed staker, uint256 stakingAmnt);
    event BLinkStakingDividendComplete(uint256 indexed round, address[] tokenAddrs, uint256[] tokenValues, uint32 stakingInfoLength, uint32 end);

    struct Dividend {
        address[] tokenAddrs;
        uint256[] tokenValues;
        uint32 start;
        uint32 end;
    }

    struct StakeSnapshot {
        uint256 totalStakingAmount;
        uint256 devStakingAmount;
        uint32 stakerCnt;
        uint32 nextStaker;
        uint32 nextStakingIndex;
        uint32 snapshotVersion;
        uint32 completeTime;
    }

    struct StakingInfo {
        address staker;
        uint256 stakingAmt;
    }

    uint64 public snapshotValidDuration = 24*60*60;

    enum Status {
        INIT,//0
        INIT_SNAPSHOT,//1
        DOING_SNAPSHOT,//2
        COMPLETE_SNAPSHOT,//3
        INIT_DIVIDEND,//4
        DOING_DIVIDEND,//5
        COMPLETE_DIVIDEND//6
    }

    Status public status;

    IBLinkStakerContract public blinkStakerIns;

    Dividend[] public dividendList;

    uint256 public nextDividendIndex;

    StakeSnapshot public stakeSnapshot;

    StakingInfo[] public stakerInfos;

    uint256 lastDivideCompleteTime=0;

    constructor() public {
        dividendList.push(Dividend(new address[](0), new uint256[](0), 0, 0));
    }

    function() external payable {}

    function setSnapshotValidDuration(uint64 _snapshotValidDuration) external onlyAdmin {
        snapshotValidDuration = _snapshotValidDuration;
    }

    function setBLinkStakerContract(address _stakerContractAddr) external onlyAdmin {
        require(_stakerContractAddr != address(0), "_stakerContractAddr is 0");
        blinkStakerIns = IBLinkStakerContract(_stakerContractAddr);
    }

    function updateStakingInfo(StakingInfo staking) internal {
        uint256 index = stakeSnapshot.nextStakingIndex;
        if (index >= stakerInfos.length) {
            stakerInfos.push(staking);
        } else {
            stakerInfos[index] = staking;
        }
        stakeSnapshot.nextStakingIndex += 1;
    }

    function createStakeSnapshot() external onlyOperator returns (uint256, uint256) {
        require(status == Status.INIT ||
        status == Status.COMPLETE_SNAPSHOT ||
        status == Status.COMPLETE_DIVIDEND, "status error");
        (uint256 devAmount, uint256 totalAmount) = blinkStakerIns.getTotalStakeBLink();
        require(devAmount > 0, "devAmount is 0");
        require(totalAmount > 0, "totalAmount is 0");

        stakeSnapshot.totalStakingAmount = totalAmount;
        stakeSnapshot.devStakingAmount = devAmount;

        stakeSnapshot.stakerCnt = uint32(blinkStakerIns.getMaxHolderIndex() + 1);
        stakeSnapshot.nextStaker = 0;
        stakeSnapshot.nextStakingIndex = 0;
        stakeSnapshot.snapshotVersion += 1;

        blinkStakerIns.setStakePause(true);

        emit SnapshotStarted(stakeSnapshot.snapshotVersion, uint64(totalAmount), uint64(devAmount), stakeSnapshot.stakerCnt);
        status = Status.INIT_SNAPSHOT;
        return (devAmount, totalAmount);
    }

    function doNextSnapshot(uint256 loopCnt) external onlyOperator returns (bool) {
        Status ss = status;
        require(ss == Status.INIT_SNAPSHOT || ss == Status.DOING_SNAPSHOT, "status error");
        require(loopCnt > 0, "loopCnt 0");

        if (ss != Status.DOING_SNAPSHOT) {
            status = Status.DOING_SNAPSHOT;
        }
        uint32 stakerCnt = stakeSnapshot.stakerCnt;

        uint32 nextStaker = stakeSnapshot.nextStaker;

        if (nextStaker == 0) {
            updateStakingInfo(StakingInfo(addrFinance, stakeSnapshot.devStakingAmount));
            nextStaker = 1;
        }

        for (uint256 i = 0; i < loopCnt; i++) {
            if (nextStaker >= stakerCnt) {
                stakeSnapshot.nextStaker = nextStaker;
                status = Status.COMPLETE_SNAPSHOT;
                blinkStakerIns.setStakePause(false);
                stakeSnapshot.completeTime = uint32(block.timestamp);
                emit SnapshotCompleted(stakeSnapshot.snapshotVersion, stakeSnapshot.totalStakingAmount, stakeSnapshot.devStakingAmount, stakeSnapshot.nextStakingIndex);
                return true;
            }
            (address staker, uint256 stakingAmount) = blinkStakerIns.getBLinkAmountByIndex(nextStaker);
            if (stakingAmount > 0 && staker!= address(0) && !staker.isContract()) {
                updateStakingInfo(StakingInfo(staker, uint64(stakingAmount)));
            }
            nextStaker += 1;
        }

        stakeSnapshot.nextStaker = nextStaker;
        return false;
    }

    function createDividend(address[] addrs) external onlyOperator returns (bool) {
        require(status == Status.COMPLETE_SNAPSHOT, "status error");
        return _createDividend(dividendList.length, addrs);
    }

    function createDividend(uint256 index, address[] addrs) external onlyAdmin returns (bool) {
        return _createDividend(index,  addrs);
    }

    function _createDividend(uint256 index, address[] addrs) internal returns (bool) {
        uint32 t = uint32(block.timestamp);
        if (t - stakeSnapshot.completeTime > snapshotValidDuration) {
            return false;
        }
        uint256[] memory tokenValues= address(this).getAllBalance(addrs);
        if (index >= dividendList.length) {
            dividendList.push(Dividend(addrs, tokenValues, t, 0));
        } else {
            dividendList[index] = Dividend(addrs, tokenValues, t, 0);
        }
        nextDividendIndex = 0;
        emit BLinkStakingDividendCreate(index,stakeSnapshot.totalStakingAmount, addrs, tokenValues, stakeSnapshot.nextStakingIndex, t);
        status = Status.INIT_DIVIDEND;
        return true;
    }

    function doNextDividend(uint256 loopCnt) external onlyOperator returns (bool) {
        Status ss = status;
        require(ss == Status.INIT_DIVIDEND || ss == Status.DOING_DIVIDEND, "status error");
        require(loopCnt > 0, "loopCnt 0");
        if (ss != Status.DOING_DIVIDEND) {
            status = Status.DOING_DIVIDEND;
        }

        uint256 roundIndex = dividendList.length - 1;
        Dividend storage dividend = dividendList[roundIndex];

        uint32 stakingIndex = stakeSnapshot.nextStakingIndex;
        uint256 totalStakingAmount = stakeSnapshot.totalStakingAmount;
        uint256 divideIndex = nextDividendIndex;

        address[] storage tokenAddrs = dividend.tokenAddrs;
        uint256[] storage tokenValues= dividend.tokenValues;

        for (uint256 i = 0; i < loopCnt; i++) {
            if (divideIndex >= stakingIndex) {
                nextDividendIndex = divideIndex;
                dividend.end = uint32(block.timestamp);
                lastDivideCompleteTime=block.timestamp;
                status = Status.COMPLETE_DIVIDEND;
                emit BLinkStakingDividendComplete(roundIndex, tokenAddrs, tokenValues, stakingIndex, dividend.end);
                return true;
            }
            StakingInfo storage staking = stakerInfos[divideIndex];
            address staker = staking.staker;
            uint256 stakingAmt = staking.stakingAmt;
            emit BLinkStakingDividendStep(roundIndex, divideIndex, staker, stakingAmt);
            _transferToStacker(staker, stakingAmt, totalStakingAmount, tokenAddrs, tokenValues);
            divideIndex += 1;
        }
        nextDividendIndex = divideIndex;
        return false;
    }

    function forwardNextDividendIndex() external onlyOperator returns(uint256) {
        require(Status.DOING_DIVIDEND == status, "status error");
        nextDividendIndex += 1;
        return nextDividendIndex;
    }

    function getCurrentDivideInfo()
    external
    view
    returns(
        uint256 round,
        uint256 curStatus,
        uint256 nextStaker,
        uint256 nextStakingIndex,
        uint256 stakerCnt,
        uint256 curDividendIndex,
        uint256 start,
        uint256 lastCompleteTime
    )
    {
        round = uint256(dividendList.length - 1);
        curStatus = uint256(status);

        Dividend storage curDivide = dividendList[round];
        curDividendIndex = nextDividendIndex;
        start = curDivide.start;
        lastCompleteTime = lastDivideCompleteTime;

        nextStaker = stakeSnapshot.nextStaker;
        nextStakingIndex = stakeSnapshot.nextStakingIndex;
        stakerCnt = stakeSnapshot.stakerCnt;
    }

    function _transferToStacker(address staker, uint256 stakingAmt, uint256 totalStakingAmount, address[] storage tokenAddrs, uint256[] storage tokenValues) internal {
        require(staker != address(0), "staker is 0");
        uint256 len = tokenAddrs.length;
        uint256[] memory stakerValues = new uint[](len);
        for (uint256 i = 0; i < len; i += 1){
            stakerValues[i]=tokenValues[i].mul(stakingAmt).div(totalStakingAmount);
        }
        staker.transferAll(tokenAddrs,stakerValues);
    }
}
