pragma solidity ^0.4.23;

import "./BaseAdmin.sol";

interface IDevBLink {
    function getDevBLinkAmount() external view returns(uint256);
}

interface IBLinkToken {
    function balanceOf(address _from) external view returns(uint256);
    function transfer(address _to, uint256 _value) external returns(bool);
    function transferFrom(address _from, address _to, uint256 _value) external returns(bool);
    function burn(uint256 _value) external returns (bool);
}

contract BLinkStaker is BaseAdmin {
    event StakeBLink(
        address indexed _owner, 
        uint256 _amount,
        uint256 _blinkTotal
    );

    event UnstakeBLink(
        address indexed _owner, 
        uint256 _oldUnfreezingAmount,
        uint256 _newUnfreezingAmount,
        uint256 _leftAmount,
        uint256 _tmFreeze
    );

    event UnstakeBLinkCancel(
        address indexed _owner, 
        uint256 _cancelAmount,
        uint256 _total
    );

    event WithdrawUnstakeBLink(
        address indexed _owner, 
        uint256 _amount
    );

    mapping (address => bool) public pauseControllerMap;
    mapping (address => uint256) public playerToIndexMap;
    mapping (uint256 => address) public indexToPlayerMap;

    /*
        不留有预挖，同步释放,释放比率
    */

    // 用户解冻的数据，什么时候可以领取解冻的blink | 解冻数量
    // time(32)|blinkAmount(28字节)
    mapping (address => uint256) playerUnFreezeMap;

    // 冻结blink的数量
    // blinkAmount
    uint256[] public playerStakeArray;

    uint constant TIME_MOVING=28*8;
    uint256 constant CLEAN_TIME=(~uint256(0))>>4*8;


    IBLinkToken blinkToken;
    IDevBLink devBLink;

    address public addrOperator;
    uint256 public stakeCd = 86400;
    uint256 public unfreezingBalance;

    constructor() public {
        addrAdmin = msg.sender;
        addrOperator = msg.sender;
        playerStakeArray.length += 1;//第一个位置不要，因为一个不存在的用户获取出来的位置就是0
    }

    modifier whenNotPaused() {
        require(!isPaused);
        _;
    }

    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a);
        return c;
    }

    function setAddrOperator(address _addr) external onlyAdmin {
        require(_addr != address(0));
        addrOperator = _addr;
    }

    function setBLinkToken(address _addr) external onlyAdmin {
        require(_addr != address(0));
        blinkToken = IBLinkToken(_addr);
    }

    function setDevBLink(address _addr) external onlyAdmin {
        require(_addr != address(0));
        devBLink = IDevBLink(_addr);
    } 

    function setStakeCd(uint256 _cd) external onlyAdmin {
        require(_cd >= 1 && _cd <= 8640000);
        stakeCd = _cd;
    }

    function getParam() external view
        returns(
            address _blinkToken,
            address _devBLink
        )
    {
        _blinkToken = address(blinkToken);
        _devBLink = address(devBLink);
    }

    function setPauseController(address _addr, bool _useful) external onlyAdmin {
        require(_addr != address(0));
        pauseControllerMap[_addr] = _useful;
    }

    function setStakePause(bool _pause) external {
        require(pauseControllerMap[msg.sender] || msg.sender == addrAdmin);
        isPaused = _pause;
    }

    function isStakePaused() external view returns(bool) {
        return isPaused;
    }

    function getTotalStakeBLink() external view returns(uint256 devAmount, uint256 totalAmount) {
        devAmount = devBLink.getDevBLinkAmount();
        uint256 stakerBLinkBalance = blinkToken.balanceOf(address(this));
        uint256 preTotalAmount = add(stakerBLinkBalance, devAmount);
        require(preTotalAmount >= unfreezingBalance);
        totalAmount = preTotalAmount - unfreezingBalance;
    }

    function getMaxHolderIndex() external view returns(uint256) {
        return playerStakeArray.length - 1;
    }

    function getDevBLinkAmount() external view returns(uint256) {
        return devBLink.getDevBLinkAmount();
    }

    function getStakeInfoByAddress(address _owner)
        external 
        view 
        returns(
            address owner,
            uint256 stakeAmount,  // 冻结中的
            uint256 tmUnfreeze,  // 解冻到期时间
            uint256 unfreezingAmount // 解冻中
        )
    {
        require(_owner != address(0));
        owner = _owner;
        uint256 index = playerToIndexMap[_owner];
        if (index > 0 && index < playerStakeArray.length) {
            stakeAmount = playerStakeArray[index];
            uint256 unfreezingVal = playerUnFreezeMap[_owner];
            unfreezingAmount = unfreezingVal & CLEAN_TIME;
            tmUnfreeze = unfreezingVal>>TIME_MOVING;
        }else{
            stakeAmount=0;
            tmUnfreeze=0;
            unfreezingAmount=0;
        }
    }

    function getStakeAmountByIndex(uint256 _index)
        external 
        view 
        returns(
            address owner, 
            uint256 amount
        ) 
    {
        if (_index > 0 && _index < playerStakeArray.length) {
            uint256 stakeVal = playerStakeArray[_index];
            owner = indexToPlayerMap[_index];
            amount = stakeVal;
        } else {
            owner = address(0);
            amount = 0;
        }
    }

    function _stakeFirst(address _from, uint256 _blinkAmount) internal {
        uint256 stakeVal = _blinkAmount;
        playerToIndexMap[_from] = playerStakeArray.length;
        indexToPlayerMap[playerStakeArray.length]=_from;
        playerStakeArray.push(stakeVal);

        emit StakeBLink(_from, _blinkAmount, _blinkAmount);
    }

    function _stakeCommon(uint256 _index, address _from, uint256 _blinkAmount) internal {
        require(_index < playerStakeArray.length);
        uint256 stakeVal = playerStakeArray[_index];
        uint256 blinkVal =  stakeVal + _blinkAmount;
        require(blinkVal >= _blinkAmount);
        require(blinkVal >= stakeVal);
        playerStakeArray[_index] =  blinkVal;

        emit StakeBLink(_from, _blinkAmount, blinkVal);
    }

    function receiveApproval(address _from, uint256 _value, bytes _extraData) external whenNotPaused {
        require(msg.sender == address(blinkToken) && _value >= 1000000);
        require(blinkToken.transferFrom(_from, address(this), _value));

        uint256 index = playerToIndexMap[_from];
        if (index != 0) {
            _stakeCommon(index, _from, _value);
        } else {
            _stakeFirst(_from, _value);
        }
    }

    /*
        从冻结中到解冻中
    */
    function Unfreeze(uint256 _amount) external whenNotPaused {

        address player = msg.sender;
        uint256 index = playerToIndexMap[player];
        require(index > 0 && index < playerStakeArray.length);

        uint256 oldStakeVal = playerStakeArray[index];
        require(oldStakeVal >= _amount);

        uint256 unfreezingVal = playerUnFreezeMap[player];
        uint256 unfreezingAmount = unfreezingVal & CLEAN_TIME;
        uint256 newUnfreezingAmount = add(unfreezingAmount, _amount);

        uint256 tmFreeze = (block.timestamp + stakeCd);

        playerStakeArray[index] = oldStakeVal - _amount;
        playerUnFreezeMap[player] = newUnfreezingAmount|tmFreeze;

        unfreezingBalance = add(unfreezingBalance, _amount);

        emit UnstakeBLink(player, unfreezingAmount, newUnfreezingAmount, oldStakeVal - _amount, tmFreeze);
    }

    /*
        解冻中的---到冻结
    */
    function CancelUnfreeze() external whenNotPaused {
        address player = msg.sender;

        uint256 unfreezingVal = playerUnFreezeMap[player];
        uint256 unfreezingAmount = unfreezingVal & CLEAN_TIME;
        require(unfreezingAmount > 0 && unfreezingBalance >= unfreezingAmount);

        uint256 index = playerToIndexMap[player];
        require(index > 0 && index < playerStakeArray.length);
        uint256 stakeVal = playerStakeArray[index];

        uint256 blinkVal = add(stakeVal, unfreezingAmount);

        unfreezingBalance -= unfreezingAmount;
        playerUnFreezeMap[player] = 0;
        playerStakeArray[index] = blinkVal;

        emit UnstakeBLinkCancel(player, unfreezingAmount, blinkVal);
    }

    /*
        从解冻中的提现
    */
    function _withdrawUnfreeze(address _player) internal {
        uint256 unfreezingVal = playerUnFreezeMap[_player];
        uint256 unfreezingAmount = unfreezingVal & CLEAN_TIME;
        uint256 tmFreeze = unfreezingVal >>TIME_MOVING;
        require(unfreezingAmount > 0 && tmFreeze <= block.timestamp && unfreezingBalance >= unfreezingAmount);

        unfreezingBalance -= unfreezingAmount;
        playerUnFreezeMap[_player] =0;

        require(blinkToken.transfer(_player, unfreezingAmount));

        emit WithdrawUnstakeBLink(_player, unfreezingAmount);
    }

    function WithdrawUnfreeze() external {
        _withdrawUnfreeze(msg.sender);
    }

    function withdrawUnfreezeForece(address _player) external {
        require(msg.sender == addrAdmin || msg.sender == addrOperator);
        require(_player != address(0));
        _withdrawUnfreeze(_player);
    }

    function unfreezeForece(uint256 _index) external whenNotPaused {
        require(msg.sender == addrAdmin || msg.sender == addrOperator);
        require(_index > 0 && _index < playerStakeArray.length);

        uint256 blinkVal = playerStakeArray[_index];
        address player = indexToPlayerMap[_index];

        if (player != address(0)) {
            uint256 unfreezingVal = playerUnFreezeMap[player]; 
            uint256 unfreezingAmount = unfreezingVal & CLEAN_TIME;
            uint256 needBack = blinkVal;
            if (unfreezingAmount > 0) {
                require(unfreezingBalance >= unfreezingAmount);
                unfreezingBalance -= unfreezingAmount;
                playerUnFreezeMap[player] = 0;
                needBack = add(blinkVal, unfreezingAmount);
            }

            if (needBack > 0) {
                emit UnstakeBLink(player, unfreezingAmount, needBack, 0, block.timestamp);

                playerStakeArray[_index] = 0;
                require(blinkToken.transfer(player, needBack));

                emit WithdrawUnstakeBLink(player, needBack);
            }    
        }    
    }
}
