pragma solidity ^0.4.23;

contract AccessAdmin {
    bool public isPaused = false;
    address addrAdmin;

    constructor() public {
        addrAdmin = msg.sender;
    }

    modifier onlyAdmin() {
        require(msg.sender == addrAdmin);
        _;
    }

    modifier whenNotPaused() {
        require(!isPaused);
        _;
    }

    function setAdmin(address _newAdmin) external onlyAdmin {
        require(_newAdmin != address(0));
        addrAdmin = _newAdmin;
    }

    function doPause() external onlyAdmin {
        isPaused = true;
    }

    function doUnpause() external onlyAdmin {
        isPaused = false;
    }
}

contract Referralship is AccessAdmin {
    event CreateReferral(
        address indexed _owner,
        string _code
    );

    event ReferralShip(
        address indexed _target,
        address indexed _mentor
    );

    mapping(address => string)  public referralCodeMap;
    mapping(bytes32 => address) public referralCodeOwner;
    mapping(address => address) public referralShipMap;
    mapping(address => bool) public safeOperator;
    mapping(address => bytes32) customerSeedMap;
    bytes32 public shareSeed;

    constructor() public {
        addrAdmin = msg.sender;
        shareSeed = keccak256(abi.encodePacked(block.coinbase, block.timestamp));
    }

    function setOperator(address _addr, bool _useful)
    external
    onlyAdmin
    {
        safeOperator[_addr] = _useful;
    }

    function getAddrByReferralCode(string _code)
    external
    view
    returns (address)
    {
        bytes32 codeHash = keccak256(abi.encodePacked(_code));
        return referralCodeOwner[codeHash];
    }

    function getReferralCodeByAddr(address _addr)
    external
    view
    returns (string)
    {
        return referralCodeMap[_addr];
    }

    function getReferralShip(address _target)
    external
    view
    returns (address)
    {
        return referralShipMap[_target];
    }


    function createReferralCode(string _code)
    external
    {
        require(bytes(referralCodeMap[msg.sender]).length == 0);
        uint256 codeLen = bytes(_code).length;
        require(codeLen >= 4 && codeLen <= 16);

        bytes32 codeHash = keccak256(abi.encodePacked(_code));
        address tmp = referralCodeOwner[codeHash];

        require(tmp == address(0));

        referralCodeMap[msg.sender] = _code;
        referralCodeOwner[codeHash] = msg.sender;

        emit CreateReferral(msg.sender, _code);
    }

    function setReferralShip(address _target, address _mentor) external {
        require(safeOperator[msg.sender] || msg.sender == addrAdmin);
        referralShipMap[_target] = _mentor;

        emit ReferralShip(_target, _mentor);
    }

    function setCustomerSeed(bytes32 _seed)
    external
    {
        customerSeedMap[msg.sender] = _seed;
    }

    function getCustomerSeed(address _target)
    external
    view
    returns (bytes32)
    {
        require(safeOperator[msg.sender]);
        bytes32 seed = customerSeedMap[_target];
        if (seed != bytes32(0)) {
            return seed;
        } else {
            return shareSeed;
        }
    }

    function getMySeed()
    external
    view
    returns (bytes32)
    {
        bytes32 seed = customerSeedMap[msg.sender];
        if (seed != bytes32(0)) {
            return seed;
        } else {
            return shareSeed;
        }
    }
}
