pragma solidity ^0.4.23;

import "./SafeMath.sol";

interface IBEP20 {
    function approve(address _spender, uint256 _value) external returns (bool);
    function balanceOf(address _from) external view returns(uint256);
    function transfer(address _to, uint256 _value) external returns(bool);
    function transferFrom(address _from, address _to, uint256 _value) external returns(bool);
}

library AddressUtil {
    using SafeMath for uint256;

    event TransferBnb(address indexed _to, uint256 _amount);
    event TransferBEP20(address indexed _to, address indexed _token, uint256 _value);


    function isContract(address _addr) internal view returns(bool) {
        uint32 size;
        assembly {
            size := extcodesize(_addr)
        }
        return (size > 0);
    }

    function transferBnb(address _addr, uint256 value) internal {
        if (value > 0){
            _addr.transfer(value);
            emit TransferBnb(_addr, value);
        }
    }

    function transferBep20(address _addr, address tokenAddr, uint256 value) internal {
        require(tokenAddr != address(0), "addr is 0");
        if (value > 0){
            IBEP20(tokenAddr).transfer(_addr, value);
            emit TransferBEP20(_addr, tokenAddr, value);
        }

    }

    /*
        应该是错误的，地址应该只有20字节，而不是21字节
    */
    function transferAll(address _addr, uint256 _bnbValue, uint256[] memory _tokens) internal {
        require(_addr != address(0), "_addr is 0");
        uint256 val = 0;
        if (_bnbValue > 0){
            transferBnb(_addr, _bnbValue);
        }

        uint256 len = _tokens.length;
        address tokenAddr;
        for (uint256 i = 0; i < len; i += 1){
            // |-- 21 Byte (address) --|-- 11 Byte (value) --|
            uint256 token = _tokens[i];
            val = token & 0xffffffffffffffffffffff; // 11 Byte
            tokenAddr = address(token >> 88); // (11*8)
            transferBep20(_addr, tokenAddr, val);
        }
    }

    function getAllBalance(address _addr,  IBEP20[] memory tokens) internal view
    returns(uint256, uint256[] memory)
    {
        uint256 bnbValue=_addr.balance;
        uint256 len = tokens.length;
        uint256[] memory tokenAndValues = new uint[](len);
        for (uint256 i = 0; i < len; i += 1 ){
            IBEP20 token = tokens[i];
            uint256 value = token.balanceOf(_addr);
            tokenAndValues[i] = (uint256(token) << 88) + (value & 0xffffffffffffffffffffff);
        }
        return (bnbValue, tokenAndValues);
    }
}
