pragma solidity ^0.4.23;

import "./BaseAdmin.sol";

interface IMoonDice {
    function __callback(address _bettor, uint256 _seed) external;
}

contract MoonDiceOraclize is BaseAdmin {
    IMoonDice moonDice;

    mapping (address => bool) public addrOperatorMap;

    constructor() public {
        addrAdmin = msg.sender;
    }

    function setAddrOperatorMap(address _addr, bool _useful) external onlyAdmin {
        require(_addr != address(0));
        addrOperatorMap[_addr] = _useful;
    }

    function setMoonDice(address _addr) external onlyAdmin {
        require(_addr != address(0));
        moonDice = IMoonDice(_addr);
    }

    function endRollDice(address _bettor, uint256 _seed) external {
        require(addrOperatorMap[msg.sender] || msg.sender == addrAdmin);
        moonDice.__callback(_bettor, _seed);
    }

}
