pragma solidity ^0.4.23;

import "./BaseAdmin.sol";
import "./SafeMath.sol";
import "./AddressUtil.sol";

interface IMoonStakerContract {
    function setStakePause(bool _pause) external;
    function getTotalStakeMoon() external view returns(uint256 devAmount, uint256 totalAmount);
    function getMaxHolderIndex() external view returns(uint256);
    function getMoonAmountByIndex(uint256 _index) external view returns(address owner, uint256 moonAmount);
}

contract MoonDividend is FinanceAdmin {
    using SafeMath for uint256;
    using SafeMath64 for uint64;
    using AddressUtil for address;

    event SnapshotStarted(uint32 snapshotVersion, uint64 totalStakingAmount, uint64 devStakingAmount, uint32 stakerCnt);
    event SnapshotCompleted(uint32 snapshotVersion, uint64 totalStakingAmount, uint64 devStakingAmount, uint32 stakingInfoLength);
    event MoonStakingDividendPlusCreate(uint256 indexed round,uint64 totalStakingAmount, uint256 bnbValue, uint256[] bep20s, uint32 stakingInfoLength, uint32 start);
    event MoonStakingDividendPlusStep(uint256 indexed round, uint256 indexed dividIndex, address indexed staker, uint64 stakingAmnt);
    event MoonStakingDividendPlusComplete(uint256 indexed round, uint256 bnbValue, uint256[] bep20s, uint32 stakingInfoLength, uint32 end);

    struct Dividend {
        uint256 bnbValue;
        uint256[] bep20s;

        uint32 start;
        uint32 end;
    }

    struct StakeSnapshot {
        uint64 totalStakingAmount;
        uint64 devStakingAmount;
        uint32 stakerCnt;
        uint32 nextStaker;
        uint32 nextStakingIndex;
        uint32 snapshotVersion;
        uint32 completeTime;
    }

    struct StakingInfo {
        address staker;
        uint64 stakingAmt;
    }

    uint64 public snapshotValidDuration = 24*60*60;

    enum Status {
        INIT,//0
        INIT_SNAPSHOT,//1
        DOING_SNAPSHOT,//2
        COMPLETE_SNAPSHOT,//3
        INIT_DIVIDEND,//4
        DOING_DIVIDEND,//5
        COMPLETE_DIVIDEND//6
    }

    Status public status;

    IMoonStakerContract public moonStakerIns;

    Dividend[] public dividendList;

    uint256 public nextDividendIndex;

    StakeSnapshot public stakeSnapshot;

    StakingInfo[] public stakerInfos;

    constructor() public {
        dividendList.push(Dividend(0, new uint256[](0), 0, 0));
    }

    function() external payable {}

    function setSnapshotValidDuration(uint64 _snapshotValidDuration) external onlyAdmin {
        snapshotValidDuration = _snapshotValidDuration;
    }

    function setMoonStakerContract(address _stakerContractAddr) external onlyAdmin {
        require(_stakerContractAddr != address(0), "_stakerContractAddr is 0");
        moonStakerIns = IMoonStakerContract(_stakerContractAddr);
    }

    function updateStakingInfo(StakingInfo staking) internal {
        uint256 index = stakeSnapshot.nextStakingIndex;
        if (index >= stakerInfos.length) {
            stakerInfos.push(staking);
        } else {
            stakerInfos[index] = staking;
        }
        stakeSnapshot.nextStakingIndex += 1;
    }

    function createStakeSnapshot() external onlyOperator returns (uint256, uint256) {
        require(status == Status.INIT ||
        status == Status.COMPLETE_SNAPSHOT ||
        status == Status.COMPLETE_DIVIDEND, "status error");
        (uint256 devAmount, uint256 totalAmount) = moonStakerIns.getTotalStakeMoon();
        require(devAmount > 0, "devAmount is 0");
        require(totalAmount > 0, "totalAmount is 0");

        stakeSnapshot.totalStakingAmount = uint64(totalAmount);
        stakeSnapshot.devStakingAmount = uint64(devAmount);

        stakeSnapshot.stakerCnt = uint32(moonStakerIns.getMaxHolderIndex() + 1);
        stakeSnapshot.nextStaker = 0;
        stakeSnapshot.nextStakingIndex = 0;
        stakeSnapshot.snapshotVersion += 1;

        moonStakerIns.setStakePause(true);

        emit SnapshotStarted(stakeSnapshot.snapshotVersion, uint64(totalAmount), uint64(devAmount), stakeSnapshot.stakerCnt);
        status = Status.INIT_SNAPSHOT;
        return (devAmount, totalAmount);
    }

    function doNextSnapshot(uint256 loopCnt) external onlyOperator returns (bool) {
        Status ss = status;
        require(ss == Status.INIT_SNAPSHOT || ss == Status.DOING_SNAPSHOT, "status error");
        require(loopCnt > 0, "loopCnt 0");

        if (ss != Status.DOING_SNAPSHOT) {
            status = Status.DOING_SNAPSHOT;
        }
        uint32 stakerCnt = stakeSnapshot.stakerCnt;

        uint32 nextStaker = stakeSnapshot.nextStaker;

        if (nextStaker == 0) {
            updateStakingInfo(StakingInfo(finance, stakeSnapshot.devStakingAmount));
            nextStaker = 1;
        }

        for (uint256 i = 0; i < loopCnt; i++) {
            if (nextStaker >= stakerCnt) {
                stakeSnapshot.nextStaker = nextStaker;
                status = Status.COMPLETE_SNAPSHOT;
                moonStakerIns.setStakePause(false);
                stakeSnapshot.completeTime = uint32(block.timestamp);
                emit SnapshotCompleted(stakeSnapshot.snapshotVersion, stakeSnapshot.totalStakingAmount, stakeSnapshot.devStakingAmount, stakeSnapshot.nextStakingIndex);
                return true;
            }
            (address staker, uint256 stakingAmount) = moonStakerIns.getMoonAmountByIndex(nextStaker);
            if (stakingAmount > 0 && staker!= address(0) && !staker.isContract()) {
                updateStakingInfo(StakingInfo(staker, uint64(stakingAmount)));
            }
            nextStaker += 1;
        }

        stakeSnapshot.nextStaker = nextStaker;
        return false;
    }

    function createDividend(IBEP20[] addrs) external onlyOperator returns (bool) {
        require(status == Status.COMPLETE_SNAPSHOT, "status error");
        return _createDividend(dividendList.length, addrs);
    }

    function createDividend(uint256 index, IBEP20[] addrs) external onlyAdmin returns (bool) {
        return _createDividend(index,  addrs);
    }

    function _createDividend(uint256 index, IBEP20[] addrs) internal returns (bool) {
        uint32 t = uint32(block.timestamp);
        if (t - stakeSnapshot.completeTime > snapshotValidDuration) {
            return false;
        }

        (uint256 bnbValue, uint256[] memory bep20s) = address(this).getAllBalance(addrs);
        if (index >= dividendList.length) {
            dividendList.push(Dividend(bnbValue, bep20s, t, 0));
        } else {
            dividendList[index] = Dividend(bnbValue, bep20s, t, 0);
        }
        nextDividendIndex = 0;

        emit MoonStakingDividendPlusCreate(index,stakeSnapshot.totalStakingAmount, bnbValue, bep20s, stakeSnapshot.nextStakingIndex, t);
        status = Status.INIT_DIVIDEND;
        return true;
    }

    function doNextDividend(uint256 loopCnt) external onlyOperator returns (bool) {
        Status ss = status;
        require(ss == Status.INIT_DIVIDEND || ss == Status.DOING_DIVIDEND, "status error");
        require(loopCnt > 0, "loopCnt 0");
        if (ss != Status.DOING_DIVIDEND) {
            status = Status.DOING_DIVIDEND;
        }

        uint256 roundIndex = dividendList.length - 1;
        Dividend storage dividend = dividendList[roundIndex];

        uint32 stakingIndex = stakeSnapshot.nextStakingIndex;
        uint64 totalStakingAmount = stakeSnapshot.totalStakingAmount;
        uint256 divideIndex = nextDividendIndex;
        uint256 bnbValue = dividend.bnbValue;
        uint256[] storage bep20s= dividend.bep20s;

        for (uint256 i = 0; i < loopCnt; i++) {
            if (divideIndex >= stakingIndex) {
                nextDividendIndex = divideIndex;
                dividend.end = uint32(block.timestamp);
                status = Status.COMPLETE_DIVIDEND;
                emit MoonStakingDividendPlusComplete(roundIndex, bnbValue, bep20s, stakingIndex, dividend.end);
                return true;
            }

            StakingInfo storage staking = stakerInfos[divideIndex];
            address staker = staking.staker;
            uint64 stakingAmt = staking.stakingAmt;
            emit MoonStakingDividendPlusStep(roundIndex, divideIndex, staker, stakingAmt);
            _transferToStacker(staker, stakingAmt, totalStakingAmount, bnbValue, bep20s);
            divideIndex += 1;
        }
        nextDividendIndex = divideIndex;
        return false;
    }

    function forwardNextDividendIndex() external onlyOperator returns(uint256) {
        require(Status.DOING_DIVIDEND == status, "status error");
        nextDividendIndex += 1;
        return nextDividendIndex;
    }

    function getCurrentDivideInfo()
    external
    view
    returns(
        uint256 round,
        uint256 curStatus,
        uint256 nextStaker,
        uint256 nextStakingIndex,
        uint256 stakerCnt,
        uint256 completeTime,
        uint256 curDividendIndex,
        uint256 start
    )
    {
        round = uint256(dividendList.length - 1);
        curStatus = uint256(status);

        Dividend storage curDivide = dividendList[round];
        curDividendIndex = nextDividendIndex;
        start = curDivide.start;
        completeTime = curDivide.end;

        nextStaker = stakeSnapshot.nextStaker;
        nextStakingIndex = stakeSnapshot.nextStakingIndex;
        stakerCnt = stakeSnapshot.stakerCnt;
    }

    function _transferToStacker(address staker, uint64 stakingAmt, uint64 totalStakingAmount, uint256 _bnbValue, uint256[] storage bep20s) internal {
        require(staker != address(0), "staker is 0");
        if (_bnbValue > 0){
            staker.transferBnb(_bnbValue.mul(stakingAmt).div(totalStakingAmount));
        }

        uint256 len = bep20s.length;
        address tokenAddr;
        for (uint256 i = 0; i < len; i += 1){
            /*
                |-- 21 Byte (address) --|-- 11 Byte (value) --|
            */
            uint256 bep20 = bep20s[i];
            /*
                11*8
            */
            uint256 val = bep20 & 0xffffffffffffffffffffff;
            tokenAddr = address(bep20 >> 88);
            staker.transferBep20(tokenAddr, val.mul(stakingAmt).div(totalStakingAmount));
        }
    }
}
