pragma solidity ^0.4.23;

import "./BaseAdmin.sol";
import "./SafeMath.sol";

interface IReferralship {
    function getAddrByReferralCode(string   _code) external view returns(address);
    function getReferralCodeByAddr(address  _addr) external view returns(string);
    function getReferralShip(address _target) external view returns(address);
    function setReferralShip(address _target, address _mentor) external;
    function getCustomerSeed(address _target) external view returns(bytes32);
}

interface IMoonPool {
    function transferToWiner(address _tokenAddr,address _to, uint256 _amount, uint256 _originalAmount) external returns(bool);
    function getPoolMoonBalance() external view returns(uint256);
    function transferMoonWithDevRelease(address _to1, uint256 _amount1, uint256 _devReleaseAmount) external;
}

interface IMoonDev {
    function getMiningMoon(uint256 _gameId, address _miner, uint256 _trxAmount) external view returns(uint256 minerCnt, uint256 devCnt);
}

contract MoonDice is BaseAdmin {
    using SafeMath for uint256;

    event DiceCreate(
        uint256 indexed _orderId,
        address indexed _bettor,
        address _tokenAddr,
        uint256 _number,
        uint256 _direction,
        uint256 _amount
    );

    event DiceResult(
        uint256 indexed _orderId,
        address indexed _bettor,
        address _tokenAddr,
        uint64  _number,
        uint64  _direction,
        uint256 _amount,
        uint64  _roll,
        uint256 _winAmount
    );

    event DiceRefund(
        uint256 indexed _orderId,
        address indexed _bettor,
        address _tokenAddr,
        uint64  _number,
        uint64  _direction,
        uint256 _amount
    );

    struct DiceOrder{
        uint32  orderId;
        address tokenAddr;
        uint16  direction;
        uint16  number;
        uint16  roll;
        uint256 tokenAmount;
    }


    address public oraclize;
    IReferralship public referralship;
    IMoonPool public moonPool;
    IMoonDev public moonDev;

    uint256 public mentorRate = 20;
    bool public referralshipEnable=false;

    mapping (address => bool) public addrOperatorMap;
    mapping (address => bool) public supportTokenMap;

    mapping (address=>uint160) public minOrderAmount;
    mapping (address=> uint256[98]) public maxOrderAmountArrMap;

    uint256[98] public winBonusArray;

    uint256 public gameId=1;

    mapping (address => DiceOrder) public betOrderMap;

    constructor() public {
        addrAdmin = msg.sender;
        initWinBonus();
    }

    modifier whenNotPaused() {
        require(!isPaused);
        _;
    }

    function setGameId(uint256 _gameId)external onlyAdmin{
        gameId=_gameId;
    }

    function enableReferralship()external onlyAdmin{
        referralshipEnable=true;
    }

    function disableReferralship()external onlyAdmin{
        referralshipEnable=false;
    }

    function setOraclizeAddr(address _addr) external onlyAdmin {
        require(_addr != address(0));
        oraclize = _addr;
    }

    function getOraclizeAddr() external view returns(address) {
        return oraclize;
    }

    function setReferralshipAddr(address _addr) external onlyAdmin {
        require(_addr != address(0));
        referralship = IReferralship(_addr);
    }

    function getReferralshipAddr() external view returns(address) {
        return address(referralship);
    }

    function setMoonPoolAddr(address _addr) external onlyAdmin {
        require(_addr != address(0));
        moonPool = IMoonPool(_addr);
    }

    function getMoonPoolAddr() external view returns(address) {
        return address(moonPool);
    }

    function setMoonDevAddr(address _addr) external onlyAdmin {
        require(_addr != address(0));
        moonDev = IMoonDev(_addr);
    }

    function getMoonDevAddr() external view returns(address) {
        return address(moonDev);
    }

    function setOperator(address _addr, bool _useful) external onlyAdmin {
        require(_addr != address(0));
        addrOperatorMap[_addr] = _useful;
    }

    function initOrderMaxAmount(address _addrToken, uint256[98] _maxOrderAmountArray) public onlyAdmin {
        uint256[98] memory maxOrderAmountArray;
        for (uint256 i = 1; i <= 96; ++i) {
            maxOrderAmountArray[i] = _maxOrderAmountArray[i];
        }
        maxOrderAmountArrMap[_addrToken] = maxOrderAmountArray;
    }


    function initWinBonus() public onlyAdmin {
        for (uint256 i = 1; i <= 96; ++i) {
            winBonusArray[i] = 985000 / i - 10000;
        }
    }

    function setMentorRate(uint256 _rate) external onlyAdmin {
        require(_rate <= 100);
        mentorRate = _rate;
    }

    function _trySetReferralCode(address _bettor, string _rcode)
        private 
        returns(address mentor) 
    {
        mentor = referralship.getAddrByReferralCode(_rcode);
        require(_bettor != mentor);
        if (mentor != address(0)) {
            referralship.setReferralShip(_bettor, mentor);
        }
    }

    /*
       _tokenAddr = 0x00 BNB
    */
    function rollDice(address _tokenAddr, uint256 _tokenAmount, uint16 _number, uint16 _direction,string _rcode)
    external
    payable
    whenNotPaused
    {
        uint256 tokenAmount=_tokenAmount;
        if(_tokenAddr==address(0)){
            tokenAmount= msg.value;
        }
        bool flag= supportTokenMap[_tokenAddr];
        require(flag);
        require(tokenAmount >= minOrderAmount[_tokenAddr]);
        DiceOrder storage order = betOrderMap[msg.sender];
        if (order.orderId > 0) {
            require(order.roll < 255);
        }
        if (_direction == 0) {
            require(_number >= 1 && _number <= 95);
            require(tokenAmount <= maxOrderAmountArrMap[_tokenAddr][_number]);
        } else if(_direction == 1) {
            require(_number >= 4 && _number <= 98);
            require(tokenAmount <= maxOrderAmountArrMap[_tokenAddr][99 - _number]);
        } else {
            require(false);
        }
        if(_tokenAddr==address(0)){
            address(moonPool).transfer(tokenAmount);
        }else{
            require(IBEP20(_tokenAddr).transferFrom(msg.sender, address(moonPool), tokenAmount));
        }
        if(referralshipEnable){
            _trySetReferralCode(msg.sender, _rcode);
        }
        order.tokenAddr= _tokenAddr;
        order.orderId += 1;
        order.direction = _direction;
        order.number = _number;
        order.roll = 255;
        order.tokenAmount = tokenAmount;
        emit DiceCreate(order.orderId, msg.sender, _tokenAddr, _number, _direction, tokenAmount);
    }

    function __callback(address _bettor, uint256 _seed) external {
        require(msg.sender == oraclize);
        DiceOrder storage order = betOrderMap[_bettor];
        bool isWin;
        uint16 roll = uint16(uint256(keccak256(abi.encodePacked(referralship.getCustomerSeed(_bettor), _seed))) % 100);
        order.roll = roll;
        if (order.direction == 0) {
            isWin = roll < order.number;
        } else {
            isWin = roll > order.number;
        }
        if(order.tokenAddr==address(0)){
            _miningMoon(_bettor,order.tokenAmount);
        }
        address mentor = referralship.getReferralShip(_bettor);
        if (isWin) {
            _sendWin(order, _bettor,mentor);
        } else {
            emit DiceResult(order.orderId, _bettor, order.tokenAddr, order.number, order.direction, order.tokenAmount, roll, 0);
        }
    }

    function _sendWin(DiceOrder storage _order, address _bettor,address mentor)
    internal
    {
        uint16 number = _order.number;
        uint256 winBonus = (_order.tokenAmount / 10000).mul(winBonusArray[_order.direction == 0 ? number : 99 - number]);
        uint256 totalWin = _order.tokenAmount.add(winBonus);
        moonPool.transferToWiner(_order.tokenAddr,_bettor,totalWin,_order.tokenAmount);
        if(referralshipEnable){
            if (mentor != 0) {
                uint256 mentorFee = winBonus.mul(mentorRate) / 10000;
                moonPool.transferToWiner(_order.tokenAddr,mentor,mentorFee,0);
            }
        }
        emit DiceResult(_order.orderId, _bettor,_order.tokenAddr, number, _order.direction, _order.tokenAmount, _order.roll, totalWin);
    }

    function _miningMoon(address _bettor, uint256 _amount) private {
        uint256 mineCnt = 0;
        uint256 devCnt = 0;
        (mineCnt, devCnt) = moonDev.getMiningMoon(1, _bettor, _amount);
        if (mineCnt > 0 || devCnt > 0) {
            moonPool.transferMoonWithDevRelease(_bettor, mineCnt, devCnt);
        }
    }

    function getOrder(address _owner)
    external
    view
    returns(
        address bettor,
        uint256 tokenAmount,
        uint32  orderId,
        uint16  direction,
        uint16  number,
        uint16  roll,
        address addrToken
    )
    {
        DiceOrder memory order = betOrderMap[_owner];
        orderId = order.orderId;
        bettor = _owner;
        if (orderId > 0) {
            tokenAmount = order.tokenAmount;
            direction = order.direction;
            number = order.number;
            roll = order.roll;
            addrToken = order.tokenAddr;
        } else {
            tokenAmount = 0;
            direction = 0;
            number = 0;
            addrToken = address(0);
            roll = 255;
        }
    }

    function getOrderByIdArray(address[] _ownerArray)
    external
    view
    returns(
        address[] bettorArray,
        uint256[] tokenAmountArray,
        uint32[]  orderIdArray,
        uint16[]  directionArray,
        uint16[]  numberArray,
        uint16[]  rollArray,
        address[] addrTokenArray
    )
    {
        uint256 length = _ownerArray.length;
        require(length > 0);
        bettorArray = new address[](length);
        tokenAmountArray = new uint256[](length);
        orderIdArray = new uint32[](length);
        directionArray = new uint16[](length);
        numberArray = new uint16[](length);
        rollArray = new uint16[](length);
        addrTokenArray = new address[](length);

        DiceOrder memory order;
        address owner;
        for (uint256 i = 0; i < length; ++i) {
            owner = _ownerArray[i];
            bettorArray[i] = owner;

            order = betOrderMap[owner];
            if (order.orderId > 0) {
                tokenAmountArray[i] = order.tokenAmount;
                orderIdArray[i] = order.orderId;
                directionArray[i] = order.direction;
                numberArray[i] = order.number;
                rollArray[i] = order.roll;
                addrTokenArray[i] = order.tokenAddr;
            } else {
                tokenAmountArray[i] = 0;
                orderIdArray[i] = 0;
                directionArray[i] = 0;
                numberArray[i] = 0;
                rollArray[i] = 255;
            }
        }
    }


    function withdrawBnb(uint256 _amount)
        external 
    {
        require(msg.sender == addrAdmin);
        require(_amount > 0 && _amount <= address(this).balance);
        addrAdmin.transfer(_amount);    
    }

    function setMinOrderAmount(address _addrToken,uint160 _amount)external returns(bool){
        require(msg.sender == addrAdmin);
        minOrderAmount[_addrToken]=_amount;
        return true;
    }

    function getMinOrderAmount(address _addrToken)public view returns(uint160){
        return minOrderAmount[_addrToken];
    }

    function enableToken(address _tokenAddr)external returns(bool){
        require(msg.sender == addrAdmin);
        supportTokenMap[_tokenAddr] = true;
        return true;
    }

    function disableToken(address _tokenAddr) external returns(bool){
        require(msg.sender == addrAdmin);
        supportTokenMap[_tokenAddr] = false;
        return true;
    }

    function getMaxOrderAmount(address _tokenAddr,uint16 _number) public view returns(uint256){
        return maxOrderAmountArrMap[_tokenAddr][_number];
    }

    function initToken(address _tokenAddr, uint160 _minAmount,uint256[98] _maxOrderAmountArray) external onlyAdmin {
        supportTokenMap[_tokenAddr] = true;
        minOrderAmount[_tokenAddr] = _minAmount;
        initOrderMaxAmount(_tokenAddr,_maxOrderAmountArray);
    }
}
